mapper.select:
select 

from tbl_maintain_order

	web_user_id = #{webUserId, jdbcType=INTEGER}

        AND maintain_order_id = #{orderId, jdbcType=VARCHAR}


        AND device_name like CONCAT('%', #{deviceName, jdbcType=VARCHAR}, '%')

select mo.maintain_order_id, mo.company_name, mo.company_id, mo.device_name, mo.state, mo.fault_description, mo.create_date, u.nickname, d.dept_name from tbl_maintain_order mo left join user u on mo.user_id = u.user_id left join dept d on mo.dept_id = d.dept_id
AND mo.maintain_order_id = #{maintainOrderId, jdbcType=VARCHAR}
AND mo.company_name like CONCAT('%', #{companyName, jdbcType=VARCHAR}, '%')
AND mo.state = #{state}

select count(*)
from tbl_maintain_order
where state = #{state}
